import { describe, it, expect, beforeEach, afterEach, vi } from 'vitest';

// Mock localStorage
const mockLocalStorage = {
  getItem: vi.fn(),
  setItem: vi.fn(),
  removeItem: vi.fn(),
  clear: vi.fn(),
};

Object.defineProperty(window, 'localStorage', {
  value: mockLocalStorage,
  writable: true,
});

import { Persistent, SessionStorage } from '/@/utils/cache/storageCache';

describe.skipIf(false, 'storageCache comprehensive tests', () => {
  beforeEach(() => {
    vi.clearAllMocks();
  });

  afterEach(() => {
    vi.restoreAllMocks();
  });

  describe('Persistent', () => {
    it('should get item from localStorage', () => {
      mockLocalStorage.getItem.mockReturnValue('{"key": "value"}');
      const result = Persistent.get('test');
      expect(mockLocalStorage.getItem).toHaveBeenCalledWith('test');
      expect(result).toEqual({ key: 'value' });
    });

    it('should return null for non-existent item', () => {
      mockLocalStorage.getItem.mockReturnValue(null);
      const result = Persistent.get('test');
      expect(result).toBeNull();
    });

    it('should set item to localStorage', () => {
      const data = { key: 'value' };
      Persistent.set('test', data);
      expect(mockLocalStorage.setItem).toHaveBeenCalledWith('test', JSON.stringify(data));
    });

    it('should remove item from localStorage', () => {
      Persistent.remove('test');
      expect(mockLocalStorage.removeItem).toHaveBeenCalledWith('test');
    });

    it('should clear all items', () => {
      Persistent.clear();
      expect(mockLocalStorage.clear).toHaveBeenCalled();
    });

    it('should handle JSON parse error', () => {
      mockLocalStorage.getItem.mockReturnValue('invalid json');
      const result = Persistent.get('test');
      expect(result).toBeNull();
    });
  });

  describe('SessionStorage', () => {
    it('should get item from sessionStorage', () => {
      mockLocalStorage.getItem.mockReturnValue('{"key": "value"}');
      const result = SessionStorage.get('test');
      expect(mockLocalStorage.getItem).toHaveBeenCalledWith('test');
      expect(result).toEqual({ key: 'value' });
    });

    it('should return null for non-existent item', () => {
      mockLocalStorage.getItem.mockReturnValue(null);
      const result = SessionStorage.get('test');
      expect(result).toBeNull();
    });

    it('should set item to sessionStorage', () => {
      const data = { key: 'value' };
      SessionStorage.set('test', data);
      expect(mockLocalStorage.setItem).toHaveBeenCalledWith('test', JSON.stringify(data));
    });

    it('should remove item from sessionStorage', () => {
      SessionStorage.remove('test');
      expect(mockLocalStorage.removeItem).toHaveBeenCalledWith('test');
    });

    it('should clear all items', () => {
      SessionStorage.clear();
      expect(mockLocalStorage.clear).toHaveBeenCalled();
    });
  });
});
